public partial class SiteMaster : System.Web.UI.MasterPage
{
    protected void Page_Load(object sender, EventArgs e)
    {
        if (Session["User"] != null)
        {
            if ((Session["User"] as User).Password_changed == "Y")
            {
                if (!Page.IsPostBack)
                {
                    User_AuthorityList myuserauthoritylist = User_AuthorityManager.GetList((Session["User"] as User).User_id);
                    Int32 i = 0;
                    Lbl_authorization.Text = Lbl_authorization.Text + "<font style='color:yellow'>";
                    foreach (User_Authority myuserauthority in myuserauthoritylist)
                    {
                        if (i > 0)
                            Lbl_authorization.Text = Lbl_authorization.Text + "," + myuserauthority.Authority_name;
                        else
                            Lbl_authorization.Text = Lbl_authorization.Text + myuserauthority.Authority_name;
                        i++;
                    }
                    Lbl_authorization.Text = Lbl_authorization.Text + "</font>";
                    lbl_online_user.Text = "UserName : <font style='color:yellow'>" + (Session["User"] as User).User_name + "</font>";
                    Lbl_user_real_name.Text = "Name : <font style='color:yellow'>" + (Session["User"] as User).Real_name + "</font>";
                    lbl_login.Text = "<a href='Account/Login.aspx?event=logout' ID='Logout' runat='server'>Log Out</a></Label>";
                    MyMenu();
                }
            }
            else
            {
                Response.Redirect("Account/ChangePassword.aspx");
            }
        }
        else
        {
            Response.Redirect("Account/Login.aspx");
        }
    }

